INSERT INTO comment ("comment",created_at,likes,dislikes,geom,project_id,user_id) VALUES
	 ('Albertplatz','2022-10-17 21:31:15.987615+02',0,0,'SRID=4326;POINT (13.745875830546566 51.062992332922136)','DDN','100'),
	 ('Fahrzeugneubeschaffung: Wie viele neue Fahrzeuge müssen hierfür beschafft werden?','2022-09-14 13:38:06.597+02',0,0,'SRID=4326;POINT (8.263827 50.010397)','0','M1'),
	 ('Flachsmarktstraße und Schusterstraße vom Individualverkehr befreien: Bitte die Flachsmarktstraße und Schusterstraße vom Individualverkehr befreien oder diesen einschränken.','2022-09-14 13:38:06.607+02',1,0,'SRID=4326;POINT (8.270846 50.002242)','0','M1'),
	 ('Flüsterschienen im City Bereich: Werden Sie künftig in den Citybereichen ''Flüsterschienen'' verlegen, wie im unteren Bereich der Gaustrasse, oder wird es wieder soziale Abstufungen geben, wie im oberen Bereich der Gaustrasse?','2022-09-14 13:38:06.612+02',1,0,'SRID=4326;POINT (8.267714 49.996227)','0','M1'),
	 ('Flüsterschienen in der Gaustraße: Wie wird In City Bereich eine Flüsterschiene verlegt oder gibt es wieder wie in der Gaustraße im unteren Bereich die Flüsterschiene und im oberen Bereich der sozial nicht so betucht ist eine normale Schiene? Wird es diese Unterscheidung auch im Konsens Neustadt zu Altstadt gehen?','2022-09-14 13:38:06.612+02',1,0,'SRID=4326;POINT (8.267859 49.99668)','0','M1'),
	 ('Freizeitnutzung am Goetheplatz sicherstellen: Wie kann die Freizeitnutzung am Goetheplatz in der Variante Hindenburgstraße sichergestellt werden, insbesondere die Nutzung durch Kinder?','2022-09-14 13:38:06.597+02',0,0,'SRID=4326;POINT (8.254801 50.010475)','0','M1'),
	 ('Freude über zweite Straßenbahnlinie durch die Neustadt: Ich freue mich auf eine zweite Straßenbahnlinie durch die Neustadt! Mit beiden Linien kann ich leben, würde aber Kaiser-Karl-Ring-Richard-Wagner-Straße-Sömmerringstraße-Sömmerringplatz-Gabelsbergerstraße-Hindenburgstraße mittlerweile eher präferieren.','2022-09-14 13:38:06.592+02',1,1,'SRID=4326;POINT (8.257465 50.011411)','0','M1'),
	 ('Führung über Kreißigstraße und Kaiser-Karl-Ring: Als Ausschluss dieser Variante wurde unter anderem der Anschluss am Bismarckplatz genannt. Ist eine Führung auf dem letzten Stück über die Kreyßigstraße zum Kaiser-Karl-Ring aufgrund der Kurvenradius nicht möglich?','2022-09-14 13:38:06.588+02',1,0,'SRID=4326;POINT (8.252261 50.012443)','0','M1'),
	 ('Goethepark Ecke Heinrich-Heine Strasse: Die blaue Trassenvariante sollte beim Eintritt in das Parkgelände geradeliniger nur den Rand des Parks streifen; ggf. die Ecken der beiden Grundstücke (Hochhaus und diese spitze-tote Gartenecke des Seniorenheim) nehmen, statt sich geschlängekt dem Parkweg zu beugen. | Als Abgrenzung der Trasse um Park hin z.B. durch halbhohes Buschwerk (Schallabsorbierend und gleich auch optische Abgrenzung). | 2) Heinrich-Heine_Strasse, der an dieser Stelle ein schmaler Weg ist, würde durch die Trasse abgeschnitten/ geschlossen werden müssen. | ein Gleisübergang ist hier schwer vorstellbar.','2022-09-14 13:38:06.598+02',0,0,'SRID=4326;POINT (8.255996 50.010137)','0','M9'),
	 ('Goetheplatz der beliebteste Platz der Neustadt: Ist bewusst, dass der Goetheplatz der beliebteste Platz der Neustadt und nur einer von zwei Grünen Plätzen innerhalb der Neustadt ist?','2022-09-14 13:38:06.599+02',1,0,'SRID=4326;POINT (8.255703 50.009875)','0','M1');
INSERT INTO comment ("comment",created_at,likes,dislikes,geom,project_id,user_id) VALUES
	 ('Goetheplatz meist genutzter Naherholungsplatz: Hindenburgstraße scheint mir die beste Lösung für Nutzerinnen in der Neustadt. Allerdings hatte die Variante im Flächennutzungsplan den Charme, dass der Goetheplatz unberührt bleibt. Der Platz ist im Laufe der Jahrzehnte eh durch Bebauung geschrumpft, ist aber heute der zentrale und meist genutzte Naherholungsplatz in der Neustadt. Das sollte man nicht leichtfertig gefährden. Stichwort Lebensqualität und Erhalt von Grünflächen.','2022-09-14 13:38:06.597+02',0,0,'SRID=4326;POINT (8.255579 50.010437)','0','M1'),
	 ('Grüne Brücke als Hindernis?: Stellt die Grüne Brücke - für die Variante über die Rheinallee - kein Hindernis da?','2022-09-14 13:38:06.593+02',0,0,'SRID=4326;POINT (8.263452 50.011101)','0','M1'),
	 ('Gute Möglichkeit den vorderen Bereich des Zollhafens anzuschließen: Eine Streckenleitung über die Rheinallee und/oder eine Verbindung über die Goethestraße direkt an den Hbf/Innenstadt und somit die bessere Anbindung des vorderen Teils des Zollhafens wäre eine gute Alternative, gerade im Hinblick auf die vielen Wohnungen und Bewohner die hier in Zukunft noch auf uns zukommen. | Allerdings stellt sich bei der Verkehrsführung über die Rheinallee die Frage, wie dies mit dem übrigen Verkehrsaufkommen vereinbar ist.','2022-09-14 13:38:06.587+02',8,1,'SRID=4326;POINT (8.26019 50.012753)','0','M25'),
	 ('Haltestelle Gutenbergcenter: Ich habe es damals schon nicht verstanden, warum beim Bau der Mainzelbahn zum Lerchenberg keine Haltestelle Gutenbergcenter gleich hinter der Kreuzung Haifaallee eingerichtet wurde. Man stelle sich eine ältere Person vor, die vielleicht nicht mehr so gut zu Fuß ist und dann mindestens 15 min mit einem ''Hackenporsche'' von der Haltestelle Wilhelm Quetsch Straße zum Gutenbergcenter laufen soll. Auch die Brücke über die Autobahn scheint mir nicht so wirklich geeignet zu sein für Fußgänger. | Außerdem ist meiner Ansicht nach der obere Parkplatz vom Gutenberg Center gut geeignet als P&amp;R Parkplatz (Autobahnnähe) außerdem ist da der Parkplatz dort schon fertig und es muss kein neues Land versiegelt werden. Wie sich das da mit den Nutzungsrechten für P&amp;R verhält kann ich leider nicht einschätzen. Und wenn man es geschickt anstellt, kann da geländeangepasst eine barrierearme Rampe zum Parkplatz realisieren.','2022-09-14 13:38:06.614+02',2,1,'SRID=4326;POINT (8.233137 49.966576)','0','M15'),
	 ('Haltestelle Housing Area / Finther Landstraße: Könnte man eine Straßenbahnhaltestelle zwischen Haltestelle Viermorgenweg und Kapellenstraße einrichten. | Markus Alvarez Gonzalez','2022-09-14 13:38:06.608+02',0,1,'SRID=4326;POINT (8.199159 50.000101)','0','M3'),
	 ('Haltestelle Münsterplatz: Wenn man künftig verstärkt auf Zweirichtungsfahrzeuge setzt, wäre es vielleicht doch eine Option, die Haltestelle Münsterplatz in Mittellage auszuführen und so den Verkehrsfluss des MIV möglichst wenig zu stören. Die Einrichtungsfahrzeuge könnten dann bei den Linien über Hbf eingesetzt werden.','2022-09-14 13:38:06.608+02',0,0,'SRID=4326;POINT (8.263378 50.000148)','0','M1'),
	 ('Haltestelle Schillerplatz 100m verlegen: Da die Strecke vom Gautor kommend Richtung Theater abbiegt wäre eine Haltestelle direkt am Fastnachtsbrunnen praktisch. Wenn man die bestehende Station Schillerplatz noch die 100 Meter zum Fastnachtsbrunnen verlegt ist ein Umsteigen mit Rollstuhl oder Kinderwagen auch kein Problem mehr.','2022-09-14 13:38:06.61+02',0,0,'SRID=4326;POINT (8.267931 49.997907)','0','M21'),
	 ('Haltestelle Schillerplatz: Die Straßenbahnen, die aus Hechtsheim auf die neue Straßenbahntrasse fahren würden, wären mit einer Haltestelle am Schillerplatz praktisch, um die Innenstadt besser zu verknüpfen. Aber die Haltestelle würde auf dem Schillerplatz selbst neben dem Fastnachtsbrunnen wegen Veranstaltungen nicht so sinnvoll sein, da sie dadurch nicht barrierefrei ausgebaut werden kann. Sinnvoller wäre die Haltestelle noch in die Gaustraße zu verlegen, bevor sie auf den Schillerplatz abbiegt, oder am Anfang der Ludwigstraße, z.B. auf Höhe des jetzigen Cafes Extrablatt oder Gutleut. Diese Standorte für die Haltestelle würde dazu dienen sie barrierefrei zu bauen.','2022-09-14 13:38:06.61+02',0,0,'SRID=4326;POINT (8.267813 49.997861)','0','M6'),
	 ('Albertplatz Kommentar von 100','2022-10-17 21:55:44.948086+02',0,0,'SRID=4326;POINT (13.746494146816957 51.062726379432064)','DDN','100'),
	 ('Oberleitungen am Gutenbergplatz bis in die 60er: Erinnert sich jemand noch daran, dass es Oberleitungen schon mal am Gutenbergplatz (bis in die 1960er hinein) gab?','2022-09-14 13:38:06.609+02',0,0,'SRID=4326;POINT (8.271919 49.999083)','0','M1');
INSERT INTO comment ("comment",created_at,likes,dislikes,geom,project_id,user_id) VALUES
	 ('117er Ehrenhof umbenennen: Wenn die Straßenbahn durch den 117er Ehrenhof geführt wird, kann man dabei das Umfeld so neu gestalten (ggf. mit Rückbenennung zu Forsterplatz, wie früher), dass die militaristische Idee nicht mehr so stark zur Geltung kommt?','2022-09-14 13:38:06.603+02',0,1,'SRID=4326;POINT (8.264877 50.007109)','0','M1'),
	 ('Abbiegen in Kaiserstraße?: Könnte die Variante Rheinallee (lila) nicht in die Kaiserstraße abbiegen und dann im weiteren Verlauf (Bauhofstr.) auf die anderen beiden Varianten Richtung Altstadt stoßen? | Zum einen deckt man so nochmal ein größeres Einzugsgebiet ab, da in dem Abschnitt Rheinallee zwischen Kaiserstraße und Große Bleiche vergleichsweise weniger Bebauung ist. | Zum anderen macht erst das die Strecke interessant für Menschen, die im Bereich Kaiserstraße/Bauhofstraße arbeiten.','2022-09-14 13:38:06.601+02',2,0,'SRID=4326;POINT (8.268113 50.009245)','0','M19'),
	 ('Aktuelle und zukünftige Verkehrsströme berücksichtigt?: Wie und inwieweit werden aktuelle und zukünftige Verkehrsströme berücksichtigt (z.B. Variante Rheinallee. Mit der Fertigstellung des Zollhafens werden dort geplant ca. 4.000 Menschen dort arbeiten und ca. 2.500 dort wohnen. Dies kann die Rheinallee m.E. mittels MIV nicht schaffen.','2022-09-14 13:38:06.583+02',2,0,'SRID=4326;POINT (8.255094 50.015417)','0','M1'),
	 ('Altbauten und Bäume: Als Anwohner in einem Altbau haben wir im 4. OG schon mit dem Verschieben u. Klirren der Gläser in der Vitrine aufgrund durchfahrender Busse zu kämpfen. Wie soll das erst mit einer Straßenbahn werden? | Setzt doch lieber wieder zusätzlich die Linie 63 ein. Diese wird schmerzlich vermisst. | Dann würden auch die schönen Bäume unbeschadet bleiben. | Die Menschen, die diese Idee haben ein Straßenbahnnetz durch die Neustadt (egal wo) zu legen, wohnen sicher nicht hier.','2022-09-14 13:38:06.59+02',2,6,'SRID=4326;POINT (8.261781 50.011937)','0','M23'),
	 ('Anbindung S-Bahnhof Mainz-Nord - Zwerchallee - Hattenbergstr./Haltestelle Zwerchallee: Über Variante Rheinallee (Lila) hinaus etwas grösser in die Zukunft gedacht, ist die Anbindung von S-Bahnhof Mainz-Nord überlegenswert. | Möglicher Zwischenhalt Schoff AG/ Eingang Werkstor/ Obere Austrasse. | Weiter über Zwerchallee (Halt Tierheim &amp; Wohnhäuser) - zur Hattenbergstr. um dort bei Halt ''Zwerchallee'' einzufädeln. | Dies entspricht bestehender Buslinie 71/ Nachtlinie 92 und ergäbe tatsächlich ein ''Netz'' im Norden von Mainz.','2022-09-14 13:38:06.58+02',0,0,'SRID=4326;POINT (8.247617 50.019189)','0','M9'),
	 ('Was wird aus der Grünen Brücke?: Was wird aus der Grünen Brücke? Stellt sie kein Problem für die Variante über die Rheinalle da?','2022-09-14 13:38:06.593+02',0,0,'SRID=4326;POINT (8.263354 50.011093)','0','M1'),
	 ('Was wurde zum Thema ''Binger Straße'' besprochen oder gar vereinbart?: Was wurde im Herbst hinter verschlossenen Türen mit ausgewählten Anwohnern zum Thema "Binger Straße" besprochen oder gar vereinbart?','2022-09-14 13:38:06.608+02',0,0,'SRID=4326;POINT (8.262269 50.000193)','0','M1'),
	 ('Weiche: Bei Störung zwischen Mainz-Mombach -Mainz -Gonsenheim eine Weiche zum vorzeitigen Wenden einbauen.','2022-09-14 13:38:06.586+02',0,0,'SRID=4326;POINT (8.233663 50.013519)','0','M1'),
	 ('Wendeschleife bei Störungsbetrieb: Bei Störungsbetrieb Richtung Straßenbahnamt eine Wendschleife zwischen REWE und Teegut einbauen.','2022-09-14 13:38:06.584+02',0,0,'SRID=4326;POINT (8.258315 50.014069)','0','M1'),
	 ('Wendeschleife: In Mainz werden Einrichtungsgelenktriebwagen eingesetzt. Diese erfordern den Einsatz von Wendeschleifen bei Betriebsstörungen, Baustellen oder Fahrten, die nicht die komplette Strecke befahren. | Werden Wendeschleifen eingeplant, um den Betrieb flexibeler gestalten zu können? | Gerade bei der Sperrung der Lu müsste doch zwingend eine Wendeschleife im Bereich Große Bleiche/Bauhofstraße/Kaiserstraße errichtet werden, wenn es keine Umleitungsstrecke in der Kaiserstraße oder Großen Bleichen gibt.','2022-09-14 13:38:06.606+02',1,0,'SRID=4326;POINT (8.268585 50.004275)','0','M3');
INSERT INTO comment ("comment",created_at,likes,dislikes,geom,project_id,user_id) VALUES
	 ('Werden für Umbau der Hindenburgstraße Parkflächen entfernt?: Wie muss man sich den geplanten Umbau in der Hindenburgstraße vorstellen? Aktuell ist das eine Sackgasse. Sollen hierfür Parkflächen entfernt werden?','2022-09-14 13:38:06.603+02',0,0,'SRID=4326;POINT (8.262593 50.006839)','0','M14'),
	 ('Wie ist die Erschließung der Variante Rheinallee?: Wie ist die Erschließung der Variante Rheinallee? Hier könnte problematisch sein, dass zumindest in Teilen potentielle Nutzer:innen nur auf einer Seite der Trasse wohnen.','2022-09-14 13:38:06.59+02',0,0,'SRID=4326;POINT (8.261439 50.012001)','0','M3'),
	 ('Wird hier auch auf Lärmschutz geachtet?: Wird hier auch auf Lärmschutz geachtet?','2022-09-14 13:38:06.591+02',0,0,'SRID=4326;POINT (8.258919 50.011897)','0','M1'),
	 ('Wo sind die Karten?: Auf www.m-wie-zukunft.de unter "Projekt"-"Pläne und Streckenentwürfe" sind derzeit keine Stadtpläne mit den Streckenvarianten zu sehen. Die drei Varianten sind nur namentlich erwähnt. Die Pläne werden hoffentlich kurzfristig abrufbar gemacht...','2022-09-14 13:38:06.604+02',0,0,'SRID=4326;POINT (8.265055 50.006009)','0','M1'),
	 ('Woraus ergibt sich die Notwendigkeit der Gabelung bei der Führung über die Rheinallee?: Woraus ergibt sich die Notwendigkeit der Gabelung bei der Führung über die Rheinallee?','2022-09-14 13:38:06.585+02',0,1,'SRID=4326;POINT (8.25804 50.013722)','0','M1'),
	 ('zusätzliche Gleisverbindung Hst. Goethestraße -&gt; Neubautrasse für alle Varianten einplanen: Bisher ist eine Verbindung der Neubaustrecke zur Bestandsstrecke über die Goethestraße nur bei zwei Variantenideen vorgesehen. Wieso nicht auch für die Variantenidee Hindenburgstraße? So könnten Busnot- bzw. Schienenersatzverkehre im Zweifelsfalle vermieden werden. | Zusatzfrage: Wird für die jeweilige Verbindung mit dem Bau von Gleisdreiecken geplant?','2022-09-14 13:38:06.594+02',1,0,'SRID=4326;POINT (8.254412 50.010931)','0','M1'),
	 ('Zusätzliche Wendeschleifen am Gautor?: Sind zusätzliche Wendeschleifen, z.B. am Gautor oder Hauptbahnhof angedacht?','2022-09-14 13:38:06.613+02',0,0,'SRID=4326;POINT (8.267415 49.994346)','0','M1'),
	 ('ÖPNV-Anbindung für Goetheplatz: Die bisherige grobe Streckenplanung führt die Strecke *am Rand* des Goetheplatzes entlang - da wird nichts geschnitten und auch keine spielenden Kinder überfahren (bei entsprechendem Gefahrenpotenzial würde man sicherlich die Grenze zwischen Platz und Strecke einzäunen). | Dass vielleicht dabei ein paar Quadratmeter des Platzes beansprucht werden müssen sollte zu verkraften sein - immerhin wird der Platz dadurch *aufgewertet*, wenn er eine solche ÖPNV-Anbindung erhält.','2022-09-14 13:38:06.596+02',0,3,'SRID=4326;POINT (8.255804 50.010664)','0','M11'),
	 ('Über Sömmerringstraße + Feldbergstraße: Alternativ zur reinen Wallaustraßen Variante könnte auch eine Kombination aus Varianten blau und rot sinnvoll sein. Im Detail würde es bedeuten. Das bei der Variante Wallaustraße ab der Feldbergstrase die Straßenbahn in Richtung Feldbergplatz abbiegen. Und weiter auf der Sömmerringstraße. Von dort an könnte man dan den verschiedenen Varianten zum Bismarkplatz Mann weiter verfolgen. So könnten man den Baumbestand auf der Rheinalle- und Hindenburgstraße erhalten hätte aber trotzdem eine optimale Erschließung der Neustadt.','2022-09-14 13:38:06.596+02',6,2,'SRID=4326;POINT (8.259369 50.010498)','0','M1'),
	 ('Anschluss AKK / Wendeschleife / Weiche bei Störungsbetrieb: Wäre es möglich stadtauswärts über den Brückenplatz zu fahren und auf die Rampe draufzufahren und dann Richtung Zollhafen zu fahren, damit hätte man zum einen direkt ein Gleis für einen möglichen Anschluss nach AKK und auch eine Wendeschleife sollte die Große Bleiche mal durch Demo, Verkehrsunfall usw. gesperrt sein. Alternativ sollte auf Höhe Landtag eine Weiche verbaut werden , um im Störungsfall zu wenden.','2022-09-14 13:38:06.605+02',0,0,'SRID=4326;POINT (8.272855 50.004931)','0','M3');
INSERT INTO comment ("comment",created_at,likes,dislikes,geom,project_id,user_id) VALUES
	 ('Anschluss der großen Bleiche direkt vom HBF: Wie sieht es mit dem Anschluss der großen Bleiche direkt vom HBF aus, da dies der Logische Anschluss wäre um über den Bahnhof zu fahren','2022-09-14 13:38:06.607+02',1,1,'SRID=4326;POINT (8.265275 50.001275)','0','M1'),
	 ('Anschluss der Neubaustrecke an Bestandsgleise: Ist geplant, alle Abzweige innerhalb der Varianten sowie insbesondere die Anknüpfungen an das Bestandsnetz als Gleisdreiecke auszuführen? Oder sind stets nur bestimmte Relationen vorgesehen?','2022-09-14 13:38:06.582+02',3,0,'SRID=4326;POINT (8.25302 50.01649)','0','M2'),
	 ('Aufwertung des Goetheplatzes: Die Trasse entlang des Goetheplatzes würde den Goethepark drastisch aufwerten.','2022-09-14 13:38:06.594+02',1,1,'SRID=4326;POINT (8.255512 50.01079)','0','M1'),
	 ('Woraus ergibt sich die Notwendigkeit der Gabelung bei der Führung über die Rheinallee?: Woraus ergibt sich die Notwendigkeit der Gabelung bei der Führung über die Rheinallee?','2022-09-14 13:38:06.585+02',0,1,'SRID=4326;POINT (8.25804 50.013722)','0','1'),
	 ('zusätzliche Gleisverbindung Hst. Goethestraße -&gt; Neubautrasse für alle Varianten einplanen: Bisher ist eine Verbindung der Neubaustrecke zur Bestandsstrecke über die Goethestraße nur bei zwei Variantenideen vorgesehen. Wieso nicht auch für die Variantenidee Hindenburgstraße? So könnten Busnot- bzw. Schienenersatzverkehre im Zweifelsfalle vermieden werden. | Zusatzfrage: Wird für die jeweilige Verbindung mit dem Bau von Gleisdreiecken geplant?','2022-09-14 13:38:06.594+02',1,0,'SRID=4326;POINT (8.254412 50.010931)','0','1'),
	 ('Begrünung der Gleise und Haltestellen: Hallo | Bei der Begrünung der Gleise sollte die aktuell verendeten klimatischen Bedingungen berücksichtigt werden. So sind heute Rasengleise eher trockene Wüsten als schöne grüne Flaschen. AusdiesenGrund sollten Alternativen wie Sedumgleise geprüft werden. Sedum ist eine Pflanzengattung aus der Familie der Dickblattgewächse (Crassulaceae) mit rund 420 Arten, die sich vor allem durch eine langanhaltende Blühzeit sowie eine große Hitzeverträglichkeit auszeichnet. In einigen großen Städten wie Köln, Düsseldorf oder Berlin wird bereits seit vielen Jahren erfolgreich Sedum gepflanzt. Auch im Darmstadt gibt es solche Gleise. Gleichzeitig könnte mit dieser Pflanze auch alle neu zu bauenden Haltestellen ausgestattet werden und so für. Eine grüne Lunge mitten in der Stadt sorgen.','2022-09-14 13:38:06.604+02',1,0,'SRID=4326;POINT (8.267212 50.005795)','0','M4'),
	 ('Bestandsnetz fußläufig zu erreichen: Variante 1 ist nicht wirklich weit weg vom Bestandsnetz. Die Entfernung kann man an den meisten Stellen in 5 Minuten zu Fuss gehen. Warum glauben Sie trotzdem, dass auch mit dieser Variante viele neue Nutzer gewonnen werden können?','2022-09-14 13:38:06.6+02',0,1,'SRID=4326;POINT (8.257063 50.009487)','0','M1'),
	 ('Binger Straße: Ich hoffe das beim Ausbau der Binger Straße mit einem Bahngleis, auch eine Weiche vom Münsterplatz kommend Richtung Hbf bedacht wurde. Dieses ermöglicht Linien aus Hechtsheim oder Heiligkreuzviertel eine Wendeschleife zu fahren bzw. eine Endstelle HBF an Wochenenden.','2022-09-14 13:38:06.607+02',1,0,'SRID=4326;POINT (8.260592 50.000349)','0','M24'),
	 ('Die Spange finde ich nicht nötig: Bei der Trasse über die Rheinallee finde ich eine Spange über die Goethestraße nicht nötig. Am Straßenbahnamt erfolgt ja ein Anschluß ans Netz. Ein Gleisbogen nach links, den Kaiser-Karl-Ring hoch, ein zweiter Gleisbogen nach rechts in die Hattenbergstraße Richtung Gonsenheim/Finthen. Fertig! | Beim Weg über die Spange ergäbe sich ein kleiner Umweg. An der Nahestraße ein Gleisbogen nach links, die Goethestraße hoch, ein zweiter Gleisbogen nach rechts, den Barbarossaring weiter, am Bismarckplatz dann ein dritter Gleisbogen nach links in die Hattenbergstraße. Macht für mich keinen Sinn, zumal durch jeden engwinkligen Gleisbogen eine Bahn ausgebremst wird. Das Geld, das man hier einsparen könnte, wäre ein vielfaches zu dem, was dann eine Erweiterung der Haltepunkte ''Bismarckplatz'' auf der Seite des Kaiser-Karl-Rings kosten würde.','2022-09-14 13:38:06.6+02',0,0,'SRID=4326;POINT (8.252625 50.009719)','0','M8'),
	 ('Engstelle Wallaustraße 101: Der Bereich auf Wallaustraße 95-99 gehört zu jenen, die nicht aufgeschüttet wurden und daher noch auf dem historischen Niveau der Neustadt liegen. Dies hat zur Folge, dass sich vor und hinter diesem Bereich Rampen auf das neue Niveau anschließen. Auf Höhe der Hausnummer 101 verengt sich der Fahrbahnquerschnitt aufgrund des dortigen Wohnhauses, so dass hier ein eingleisiger Straßenbahn-Streckenabschnitt mitunter notwendig werden könnte. | Bei dem direkt an dieses Grundstück anschließenden Projekt der Wohnbau Mainz (Karolin-Stern-Platz) war ursprünglich angedacht, das Gebäude in der Wallaustraße 101 zu integrieren. Der/die Besitzer/in wollte aber damals schon nicht verkaufen. Eine Umgestaltung des Straßenraums im Zuge der Sozialen Stadt ist hierdurch an dieser Stelle wegen der Garagen im Erdgeschoss der Hausnummer 101 nicht möglich gewesen. | Gibt es neben Eingleisigkeit und Enteignung andere Lösungswege?','2022-09-14 13:38:06.584+02',2,0,'SRID=4326;POINT (8.255198 50.014266)','0','M2');
INSERT INTO comment ("comment",created_at,likes,dislikes,geom,project_id,user_id) VALUES
	 ('Erholungsflächen und Kitas: Variante 1 würde entlang wichtiger Erholungsflächen (Goethepark, Valenciaplatz) sowie 3 Kitas geführt. Das spricht gegen diese Variante und für die anderen beiden.','2022-09-14 13:38:06.601+02',0,2,'SRID=4326;POINT (8.257073 50.00947)','0','M1'),
	 ('Erweiterung auch in Richtung Weisenau: Ich würde mir eine Straßenbahnerweiterung nach MZ Weisenau freuen. Die Verkehrsanbindung über die Busse ist vor allem am Wochenende eher schlecht, gleichwohl das Viertel direkt an der Innenstadt liegt. Außerdem führen alle Busse in der Innenstadt über die gleiche Route, was m.E. sehr ineffizient ist. | Zurück zur Straßenbahn: Die Rheinallee könnte als Verlängerung der Strecke genutzt werden. Schließlich könnte die Straßenbahn die lauten Busse ablösen und damit den Verkehr entlasten. Außerdem staut es sich häufig auf der Rheinallee aufgrund der vielen Fahrzeuge, sodass Busse Verspätung haben. Das wäre mit einer Straßenbahn behoben.','2022-09-14 13:38:06.613+02',5,0,'SRID=4326;POINT (8.303945 49.983432)','0','M17'),
	 ('Erweiterung nach Mombach?: Ist bereits eine Erweiterung des Straßenbahnnetzes über die Rheinallee nach Mombach (zum Mombacher Kreisel) angedacht?','2022-09-14 13:38:06.562+02',5,0,'SRID=4326;POINT (8.22097 50.028077)','0','M1'),
	 ('Fahrradstraße durch die Hindenburgstraße: Inwieweit ist die Trassenplanung mit der Planung für ein Radverkehrsnetz abgestimmt? Durch die Hindenburgstraße soll eine Fahrradstraße führen.','2022-09-14 13:38:06.602+02',0,0,'SRID=4326;POINT (8.259747 50.008196)','0','M1'),
	 ('Haltestellte ''Hbf West'': Etwas jenseits der eigentlichen Thematik: Bitte im Zusammenhang mit der Spange durch die Binger Str. prüfen, ob die Haltestellte "Hbf West" auf die Überführung über die Gleise der DB verlegt und von dort möglichst unmittelbar oder notfalls auf der Nordseite Abgänge insbesondere auf die Bahnsteige 2/3 und 4/5 errichtet werden können. Das würde den Entfall der Haltestelle Hbf kompensieren.','2022-09-14 13:38:06.609+02',2,0,'SRID=4326;POINT (8.258783 49.999493)','0','M1'),
	 ('Haltstelle zwischen Gautor und Höfchen: Alle Linien aus Richtung Hechtsheim würden ja vom Gautor kommend am Schillerplatz noch vor der gleichnamigen Haltstelle abbiegen und erst am Höfchen wieder Halt machen. Ist eine neue Haltestelle in der Ludwigstraße angedacht oder gar nicht sinnvoll?','2022-09-14 13:38:06.612+02',3,0,'SRID=4326;POINT (8.267848 49.997854)','0','M2'),
	 ('Hindenburgstraße als zentrale Fahrradroute: Es gab von der Stadt einmal die Idee die Hindenburgstraße zur Fahrradstraße zu machen. Auch einige Verbände fordern die Hindenburgstraße als zentrale Fahrradroute auszubauen. Gibt es diese Pläne noch und was würde geschehen, wenn die Straßenbahn da durch fahren würde?','2022-09-14 13:38:06.602+02',1,0,'SRID=4326;POINT (8.260115 50.008025)','0','M1'),
	 ('Individualverkehr auf der Rheinallee: Was passiert mit dem Individualverkehr bei der Rheinallee Variante ?','2022-09-14 13:38:06.589+02',1,0,'SRID=4326;POINT (8.261045 50.012274)','0','M1'),
	 ('Ist die Wallaustraße breit genug?: Hat die Wallaustraße denn genügend Platz bzw. ist die Straße breit genug für eine Straßenbahnlinie? Und müssten die zahlreichen Parkplätze dafür geopfert werden?','2022-09-14 13:38:06.59+02',1,0,'SRID=4326;POINT (8.25892 50.01193)','0','M1'),
	 ('Kaiser-Wilhelm-Ring in die Planung einbezogen?: In wie weit wird die vorhandene Struktur der Straßenbahnenlinien in der Neustadt im Kaiser-Wilhelm-Ring in die Planung einbezogen? Hier ist eine Infrastruktur für die Neustadtquerung bereits vorhanden.','2022-09-14 13:38:06.601+02',0,1,'SRID=4326;POINT (8.252286 50.008721)','0','M1');
INSERT INTO comment ("comment",created_at,likes,dislikes,geom,project_id,user_id) VALUES
	 ('Kastell Bahnhof / Brückenkopf Kastell: Hallo | Unabhängig vom Bürgerentscheid in Wiesbaden. Sollte Mainz zusammen mit Wiesbaden eine Straßenbahn bis zum Kastell Bahnhof / Brückenkopf Kastell planen. Mit Umsetzung eines solchen Projektes ergebesich die Möglichkeit große Teile der Mainzer Innenstadt autofrei oder Autoarm (nur für Anlieger) umzubauen. Hierfür sollte Park und raid Anlagen entstehen und die THB Brücke von 4 Fahrspuren auf 2 reduziert werden. Die Straßenbahn könnte dan ein Großteil der Verkehrslast aufnehmen. Breitere Rad und Fußwege wären auch möglich.','2022-09-14 13:38:06.601+02',4,0,'SRID=4326;POINT (8.281846 50.008213)','0','M4'),
	 ('KEINE Straßenbahn über den Goetheplatz: Der Goetheplatz wurde als einer der wichtigsten öffentlichen Stadträume der Neustadt unter intensiver Beteiligung von Stadtteilakteurinnen und Akteuren im Rahmen des Städtebauförderprogramms Soziale Stadt umgestaltet. Investitionen von ca. 1.756.000,00 Euro ermöglichten den Bau eines Wasserspielplatzes sowie die Neugestaltung von Aufenthaltsbereichen, Wegen und Grünanlagen. | Daneben ist der Goetheplatz ein bedeutender Spiel- und Erholungsraum für die Bürgerinnen und Bürger der Mainzer Neustadt. Kinder, Jugendliche, Studentinnen, Studenten und Familien unter-schiedlichster Herkunft nutzen dieses Areal unabhängig der Jahreszeiten als Kommunikations-, Spiel- und Ruhepunkt. Gerade in den Sommermonaten dient der Wasserspielplatz als Freibadersatz und erfreut sich über die Quartiersgrenzen hinaus als beliebter Treffpunkt. | Liebe Mainzer Mobilität, kommen Sie doch mal an einem sonnigen Nachmittag vorbei und genießen sie den Platz....OHNE Straßenbahn!!!','2022-09-14 13:38:06.598+02',0,0,'SRID=4326;POINT (8.254927 50.010362)','0','M18'),
	 ('Konflikt mit Planungen für Fahrradstraße / Fahrradachse zur Innenstadt: Die Hindenburgstraße war/ist (?) von der Stadtverwaltung als Fahrradstraße vom Bismarckplatz zur Bauhofstraße vorgesehen und auch im Mapathonprojekt des ADFC zur Entwicklung eines Radverkehrsnetzes der Stadt Mainz (http://mapathon.adfc-mainz.de/) als wichtige Verbindungsachse ausgewählt worden. Sofern Schienen für die Straßenbahn rechts und links entlang dem Bordstein gelegt werden, wird auf Grund des Straßenquerschnitts vermutlich der Platz für eine Fahrradstraße in der Mitte nicht mehr ausreichen. Gleichzeitig müsste dann die Straße komplett für Kfz gesperrt werden, da für Kfz-Gegenverkehr sicherlich zu wenig Platz verbleibt. Dies dürfte wegen der Erschließungsfunktion der Hindenburgstraße nicht möglich sein. | Den Radverkehr über Parallelstraßen (Forsterstraße/Leibnitzstraße) zu leiten, ist nicht sinnvoll, da an der Kaiserstraße kein Übergang in Richtung Innenstadt besteht.','2022-09-14 13:38:06.603+02',15,1,'SRID=4326;POINT (8.261075 50.007491)','0','M13'),
	 ('Kreisel am Bismarckplatz: Wenn man am Bismarckplatz einen Kreisel einrichtet, könnte das Gleisdreieck oder -viereck in einer Grünfläche realisiert werden.','2022-09-14 13:38:06.588+02',3,1,'SRID=4326;POINT (8.248794 50.012563)','0','M1'),
	 ('Ludwigsstraße/Gutenbergplatz/Höfchen ungünstig: Schon heute kommen Busse kaum vernünftig durch die Ludgwigsstraße sowie Gutenbergplatz und Höfchen. Hier sind viele Leute flanierend auf den Flächen unterwegs, die für den motorisierten Verkehr vorgesehen sind, und bemerken nicht, dass sie den ÖPNV stören und teilweise in Gefahr sind. Das bedeutet auch eine erhöhte Unfallgefahr, wenn dann auch noch die Straßenbahn dazu kommt. (und ich bin für ein besseres Straßenbahnnetzt!) | Zudem kommen zahlreiche Veranstaltungen auf dem Gutenbergplatz, wodurch häufiger mit Störungen, Verspätungen und/oder Umleitungen bzw. SEV zu rechnen ist. | Bitte mal überlegen und prüfen, ob es möglich ist die Straßenbahn von der Alicenbrücke kommend über die Binger Straße, Münsterplatz, Große Bleiche dann Flachsmarktstraße, Schusterstraße, Quintinstraße, Rheinstraße, Peter-Altmeier-Alle und dann weiter in die Neustadt zu führen. | Bei dieser Streckenführung könnten z.B. Haltstellen Landesmuseum, Schusterstraße (in Höhe Kaufhof / Stadthausstraße), Rheingoldhalle/Rathaus, Landtag /Schloss entstehen, die auch eine gute Anbindung der Innenstadt sowie besonderer Gebäude mit hohem Publikumsaufkommen ermöglichen... und auch die Haltestelle Höfchen entlasten, die heut schon überquillt.','2022-09-14 13:38:06.609+02',1,2,'SRID=4326;POINT (8.271493 49.99892)','0','M20'),
	 ('Ludwigstrasse problematisch: Ich halte den Fahrweg über die Ludwigstr. für falsch. Vor dem Theater finden an sehr vielen Tagen im Jahr große Veranstaltungen statt und ein Straßenbahnverkehr ist entweder nicht möglich oder sehr gefährlich. | Eine LInienführung durch die komplette Große Bleiche erscheint mir wesentlich sinnvoller. | Warum ist diese Linienführung ausgeschlossen worden? | Wie will man denn mit Großveranstaltungen vor dem Theater zukünftig umgehen?','2022-09-14 13:38:06.605+02',1,4,'SRID=4326;POINT (8.269814 50.005072)','0','M16'),
	 ('Lärmschutz und Parkraumsituation: Wie soll an diesem Standort Lärmschutz sichergestellt werden? An diesem Standort wird bereits seit 5 Jahren durchgehend an verschiedenen Stellen gebaut (Wohnbau und andere Immobiliengesellschaften inklusive Straßenumbauten). Wie können Anwohner hinsichtlich der Lärmbelastung entlastet werden, wenn eine weitere Großraumbaustelle entsteht? (Stichwort: Bauen am Wochenende, früher Baubeginn etc.) | Wie soll der Lärmschutz für die dann neue Bahn gestaltet werden? Die Abstände zu den Häusern wird gering sein. | Werden weitere Grünflächen weichen müssen? (vglw. nach. dem Umbau um die Wallaustraße 84-99a) | Wie wird sich das Anwohnerparken gestalten, wein viele weitere Parkplätze wegfallen?','2022-09-14 13:38:06.587+02',3,3,'SRID=4326;POINT (8.257486 50.012863)','0','M22'),
	 ('Ohne Oberleitung: Hallo | Städte wie Luxemburg oder Bordeaux und Nizza zeigen uns wie es gehen könnte. Durch Akkus, Kondensatoren oder Induktion können heute schon Straßenbahn angetrieben werden das zeigen die genannten Städte seit Jahren. Bei den Planungen zur City Bahn gab es auch solche Überlegungen. | Durch den nun beginnenden Ausbau in Mainz und anstehenden Ablösung der älteren Straßenbahn (M8C und GT6-M) wäre die Möglichkeit da Fahrzeuge mit der entsprechenden Technik anzuschaffen. Die fahre neuen Fahrzeuge könnten beides mit und ohne Oberleitungen. | Durch die Einführung könnte man auf Oberleitungen in sensiblen Bereichen wie der Hindenburg Straße oder auf der Lu versichten (auf den anderen und im bestehenden netzt könnte man weiter mit Oberleitungen fahren). Die Akzeptanz erhöhen und den Eingriff in den Baumbestand würde sich erheblich verringern oder könnte sogar entfallen. Das alles zu einen geringen mehr kosten Aufwand.','2022-09-14 13:38:06.603+02',3,3,'SRID=4326;POINT (8.262491 50.006884)','0','M4'),
	 ('Parkhaus anbinden: Es gibt nach wie vor sehr viele Menschen, die mit dem Auto in die Mainzer Neustadt und Innenstadt fahren. Um dies etwas zu reduzieren wäre es sicher gut den Autofahrenden die Möglichkeit zu geben, preiswert außerhalb zu parken und dann schnell und komfortabel mit der Straßenbahn in die Innenstadt/Neustadt zu gelangen. | Hierfür möchte ich vorschlagen die Straßenbahn bis zu der Stelle zu führen, wo die Hochbrücke auf die Rheinallee trift (s. Karte) und dort Parkmöglichkeiten für Autos zu schaffen. Ggf. könnten Parkplätze vom Fahrrad Franz XXL mitgenutzt werden, dort ein zweites Parkdeck geschaffen werden oder der Platz für Parkraum genutzt werden, der dort entsteht, wo die Hochbrücke abgerissen wird. | Alle Autofahrenden, die in die Neustadt oder Innenstadt möchten, könnten dann dort parken und mit der Straßenbahn schnell und bequem in die Innenstadt oder Neustadt gelangen. | Die Straßenbahn über die Rheinallee in die Innenstadt zu führen bietet sich bei dem Vorschlag besonders an, weil es dann vermutlich die zeitlich schnellste Verbindung in die Innenstadt ist (wenig Kurven und höchst mögliche Geschwindigkeit), aber prizipiell wäre die Idee auch mit den anderen Routenführungen umsetzbar.','2022-09-14 13:38:06.58+02',2,0,'SRID=4326;POINT (8.238705 50.022454)','0','M1'),
	 ('Passt eine Straßenbahn unter der Grünen Brücke durch?: Passt eine Straßenbahn unter der Grünen Brücke durch? Oder müsste sie dann weg?','2022-09-14 13:38:06.592+02',0,0,'SRID=4326;POINT (8.263351 50.011108)','0','M27');
INSERT INTO comment ("comment",created_at,likes,dislikes,geom,project_id,user_id) VALUES
	 ('Quartiersgarage: Ein Vorteil der Variante über die Rheinallee: Das Parkhaus Quartiersgarage kann sehr bequem für Park + Ride genutzt werden. Mit dem Auto von der AS Mainz-Mombach über die Rheinallee in die Quartiersgarage, von da mit der Straßenbahn weiter Richtung Innenstadt. Die Linie ließe sich auch sehr gut bis zum Bahnhof Mainz-Nord erweitern und perspektivisch auch nach Mombach im Norden und Richtung Weisenau im Süden.','2022-09-14 13:38:06.581+02',4,0,'SRID=4326;POINT (8.252623 50.017476)','0','M1'),
	 ('Querverbindung durch die Große Bleiche: Ist es möglich, neben der Querverbindung Ludwigsstraße auch noch eine Querverbindung durch die Große Bleiche zu haben? Je engmaschiger der Netz, desto flexibler (siehe Feste etc), aber ein Umbau des Straßenraumes in der Großen Bleiche tut wirklich not! Hier hatten wir auch gute Fortschritte gemacht bei der Trassendiskussion zur CityBahn','2022-09-14 13:38:06.606+02',0,0,'SRID=4326;POINT (8.267271 50.002902)','0','M1'),
	 ('Rheinallee ist der logische Schluss: Gonsenheim und Finthen fahren ja schon in die Neustadt, da gibt es die Straba durch den Kaiser-Wilhelm-Ring. Welchen Sinn ergibt es, wenn durch die Hindenburgstr. In 200 m parallel 2 Linien führen würden? Ist nicht die Rheinallee der logische Schluss, um einen Ring zu erhalten?','2022-09-14 13:38:06.584+02',6,1,'SRID=4326;POINT (8.257266 50.014269)','0','M1'),
	 ('Rheinallee schlechteste Variante: Ein Streckenverlauf über die Rheinallee hätte deutlich weniger potentielle Fahrgäste im Einzugsgebiet, da der Rhein sehr nahe ist. Außerdem wäre der Einfluss auf den Individualverkehr auf der hochfrequentierten Rheinachse sehr groß. Entsprechend umgekehrt ist der Einfluss von Staus auf die Straßenbahn sehr hoch, da hier mit Sicherheit dem Individualverkehr keine Spur weggenommen werden kann. Die in der Auftaktveranstaltung bereits angesprochenen Reisezeiten aus Richtung Gonsenheim/Finthen sprechen ebenfalls gegen diese Variante.','2022-09-14 13:38:06.589+02',5,20,'SRID=4326;POINT (8.260925 50.01235)','0','M1'),
	 ('Rheinhessentram: Hallo | Seit vielen Jahren wird über ein Straßenbahn über Mainz hinaus drüber nachgedacht. Bis zum scheitern der Stadtbahn in Wiesbaden 2001 war eine Idee die ehemalige Strecke Budenheim Alzey mit einzubinden. Leider scheitern beide Projekte 2001 aus bekannten Gründen. Die | Rheinhessen Tram würde von der bestehende Linie 52 abzweigen und über Bretzenheim Hechtsheim, zur Messe und weiter bis nach Alzey führen und damit eine wiederbelebung der ehem. DB-Strecke Bodenheim-Alzey darstellen.','2022-09-14 13:38:06.615+02',3,0,'SRID=4326;POINT (8.251419 49.914577)','0','M5'),
	 ('Rheinufer nutzen?: Vielleicht sollte man, auch um Konflikte im Bereich der Theodor-Heuss-Brücke und der großen Bleiche zu vermeiden, die Strecke hinter der Rheingoldhalle am Rheinufer entlang laufen lassen? Mindestens bis zur Kreuzung an der Kaiserstraße. Auch für die Fahrgäste ein bestimmt angenehmerer Blick als auf die Blechlawinen in der Rheinallee/Peter-Altmeier-Allee.','2022-09-14 13:38:06.606+02',0,2,'SRID=4326;POINT (8.274968 50.003985)','0','M4'),
	 ('Ringverbindung Straßenbahn: Eine knifflige Aufgabe, am Schillerplatz beginnend, müsste vermutlich ein Gleisdreieck entstehen, wobei der Fastnachtsbrunnen schon heimliches Wahrzeichen ist und vielbesucht, er würde eingeengt. Auf der Ludwigsstraße selbst sind oft Sperrungen zu erwarten. Eher die Quintinstraße könnte Straßenbahnen und Haltestelle aufnehmen. Man könnte die von einem Bahnhofstunnel Binger Schlag-Hbf-Kaiserstraße und Bauhofstraße her erreichen. Auf der Rheinallee wird durch die Gradlinigkeit, Breite und gute Anschlüsse der Umweg wieder wettgemacht. Die gute Kurvenmöglichkeit zum Kaiser-Karl-Ring ist besser als quietschkurvenartige Verbindungen über Sömmerringplatz oder von und zur Goethestraße. Durch die Hindenburgstraße müsste am Ende der belebte Goetheplatz beschnitten werden. In der Wallaustraße gibt&#039;s zwei Senken vom ursprünglichen Bodenniveau, auch sehr ungünstig für Schienenverkehr find&#039; ich.','2022-09-14 13:38:06.583+02',0,0,'SRID=4326;POINT (8.252707 50.016178)','0','M8'),
	 ('Schlosspark: Sollte die lila Variante über die große Bleiche zum Zuge kommen, wäre eine Verkehrsberuhigung im Abschnitt zwischen Kaiser-Friedrich-Straße und Peter-Altmaier-Allee wünschenswert. Nicht erst im Zuge einer möglichen Austragung der Bundesgartenschau steht der Ernst-Ludwig-Platz im Fokus, hier eine neue große innerstädtische Grünfläche zu entwickeln. Für die Aufenthaltsqualität wäre daher der Umbau zu einer reinen Umweltachse (Fuß-Rad-ÖPNV) zu begrüßen. Der Straßenquerschnitt könnte in diesem Zuge eventuell sogar verkleinert und so mehr Platz für mehr Grün gesorgt werden.','2022-09-14 13:38:06.604+02',6,0,'SRID=4326;POINT (8.270334 50.005491)','0','M12'),
	 ('Schnellste Strecke Gonsenheim/Finthen &lt;-&gt; Höfchen: Aber die Relation Gonsenheim/Finthen &lt;-&gt; Höfchen wäre doch weiter am Schnellsten über Hbf und Schillerplatz?','2022-09-14 13:38:06.605+02',0,0,'SRID=4326;POINT (8.218376 50.005041)','0','M2'),
	 ('Schwierig... um auf die im FNP genannte Variante zurückzukommen...: Die ursprünglich im Flächennutzungsplan genannte Variante über den Sömmerringplatz würde diesen zerschneiden. Der Platz wird von Bewohnern der Nachbarhäuser und des Seniorenheims im Raupelsweg gut besucht. Aktuell fährt Linie 62 am Sömmerringplatz vorbei; die Straße ist sehr kurvenreich und damit wohl kaum eine Alternative für eine Straßenbahn. In der Sömmerringstraße selbst geben Busse häufig nochmal richtig Gas, so dass dies in den Wohnungen durchaus spürbar ist. Ich kann mir nicht vorstellen, dass die Straßenbahn in dieser Hinsicht eine Verbesserung mit sich bringt. Aufgrund der Enge der Straße rechnen wir mit massiven Schäden an Gebäuden und Unterkellerungen durch die Vibrationen der schweren Straßenbahnen. Zusätzlich dürfte der Lärmpegel durch die Bremsgeräusche und Fahrten in den Kurven ebenfalls massiv ansteigen.','2022-09-14 13:38:06.598+02',0,0,'SRID=4326;POINT (8.258768 50.010238)','0','M1');
INSERT INTO comment ("comment",created_at,likes,dislikes,geom,project_id,user_id) VALUES
	 ('Sondersignalanlage verschieben: Könnte man die Sondersignalanlage vor dem Übergang zum Hartmühlenweg legen, denn wenn für Fußgänger /Radfahrer Grün ist , quetschen sich die Fahrradfahrer:innen auf den kleinen Überweg, weil die Tram den Überweg blockiert.','2022-09-14 13:38:06.6+02',0,0,'SRID=4326;POINT (8.234816 50.009505)','0','M7'),
	 ('Spielende Kinder auf dem Goetheplatz: (Hintenburglinie) Da sind spielende Kinder auf den Goetheplatz.','2022-09-14 13:38:06.599+02',0,0,'SRID=4326;POINT (8.255023 50.010091)','0','M3'),
	 ('Spielplätze und Grünflächen nicht verdrängen: Die Neustadt wird aktuell von der Politik als ein Vorzeige Quartier immer wieder hervorgehoben. Insbesondere auch wegen der Vielfalt die sich hier bei den Anwohnern entwickelt hat. Für junge Familien mit Kindern sind Spielplätze sehr wichtig und auch Grünflächen für alle Anwohner. Diese sollten nicht durch eine Straßenbahn beeinträchtigt werden. Wie fließt dies ein?','2022-09-14 13:38:06.599+02',1,0,'SRID=4326;POINT (8.258318 50.010008)','0','M1'),
	 ('Straßenbahn in Binger Straße unabhängig vom Individualverkehr?: Werden die Straßenbahnen auf der Binger Str. unabhängig vom Individualverkehr geführt?','2022-09-14 13:38:06.608+02',0,0,'SRID=4326;POINT (8.262133 50.000199)','0','M1'),
	 ('Streckenführung zum Heiligkreuzviertel: Es gibt Überlegungen, die Streckenführung zum Heiligkreuzviertel durch Schrebergärten und andere Grünflächen zu leiten. Ist dies eine Zukunftsorientierte Verkehrsplanung? Muss nicht stattdessen der MIV weichen und nicht die wichtigen grünen Lungen?','2022-09-14 13:38:06.614+02',1,0,'SRID=4326;POINT (8.283509 49.979868)','0','M1'),
	 ('Streckenführung über Frauenlobstraße auf die Rheinallee: Ich könnte mir vorstellen, dass es Sinn machen könnte die Straßenbahn über die Frauenlobstraße auf die Rheinallee zu führen. Denn, etwa ab Höhe der Frauenlobstraße gibt es Anlieger auf beiden Seiten der Rheinallee. Ich würde mich freuen, wenn diese Alternative mit geprüft wird.','2022-09-14 13:38:06.599+02',3,1,'SRID=4326;POINT (8.265938 50.010009)','0','M1'),
	 ('Trasse Rheinallee mit städtebaulichem Potential: Die Trasse entlang der Rheinallee bietet das städtebaulich größtes Potential - hier könnte die Straße drastisch verschönert werden und auch der Radverkehr entlang einer schnellen Rheinachse könnte profitieren.','2022-09-14 13:38:06.588+02',4,0,'SRID=4326;POINT (8.260909 50.012362)','0','M1'),
	 ('Trasse von der Philippschanze zum Heiligkreuzviertel: Kann man die Straßenbahntrasse von der Philippschanze über die Goldgrube zum Heiligkreuzviertel verlängern?','2022-09-14 13:38:06.613+02',0,0,'SRID=4326;POINT (8.266799 49.989225)','0','M1'),
	 ('Trassierung Goethestraße/Nahestraße: Die Goethestraße/Nahestraße verfügt über getrennt geführten Richtungsverkehr mit doppelter Baumreihe in Mittellage inkl. Parkplätzen. Wie ist die Trassierung hier angedacht? Sollen die Bahnen im Verkehr mitschwimmen oder ist angedacht, eine Richtungspur zur ÖPNV Trasse umzuwidmen und den Verkehr des MIV auf der anderen Seite zu bündeln? Wie sieht es außerdem mit den Kurvenradien aus? Gerade die Anschlüsse an die Wallaustraße als auch die Kaiserringe erscheinen relativ eng je nach Trassenlage.','2022-09-14 13:38:06.591+02',1,0,'SRID=4326;POINT (8.254955 50.011501)','0','M1'),
	 ('Umfahrung Goetheplatz: Die blaue Variante hat nachvollziehbar ihren Charme (gerade Strecke, wenige Kurven, kurze Trasse, zentrale Erschließung). Im Kontext der wenigen Grünflächen in der eng bebauten Mainzer Neustadt ist ein Eingriff auf dem Goetheplatz sowohl in den Baumbestand als auch den Wegfall von Aufenthaltsflächen vermutlich nur schwer vermittelbar. Gibt es nicht-lösbare Konflikte, die eine Umfahrung des Platzes über Gabelsbergerstraße und Sömmeringstraße wie im FNP vorgesehen unmöglich oder zumindest nicht sinnvoll werden lassen? Die Umfahrung des Goetheplatzes würde man sich mit einem Eingriff ins Grün an anderer Stelle, einer längeren Fahrzeit, mehr Kurven und einer längeren und damit teureren Trasse erkaufen. Aber eventuell ist es ein Kompromiss, der sich lohnt zu betrachten?','2022-09-14 13:38:06.594+02',3,0,'SRID=4326;POINT (8.258205 50.011002)','0','M2');
INSERT INTO comment ("comment",created_at,likes,dislikes,geom,project_id,user_id) VALUES
	 ('Unterirdische Strecken mit Haltestellen?: Wird auch einmal überlegt, unterirdisch Strecken mit Haltestellen zu verlegen, um Raum zu gewinnen? Ich denke bspw. an die Etage unterm Hauptbahnhof von Binger Straße her mit direkten Umsteigemöglichkeiten und Shops und Erschließung über Kaiserstraße-Quintinsstraße zur Rheinallee.','2022-09-14 13:38:06.607+02',0,0,'SRID=4326;POINT (8.26012 50.000534)','0','M4'),
	 ('Variante 1 - Eingänge von Kitas in umittelbarer Nähe zu möglicher Straßenbahn: Wie auf der Karte ersichtlich, führt Variante 1 direkt am Eingang der städtischen Kita Goetheplatz vorbei. Aktuell gehen die Kinder durch das Tor, überqueren einen schmalen Weg und stehen direkt auf der Wiese. Von dort sind es nur wenige Meter bis zu Spielplatz. Sollte die Straßenbahn über den Goetheplatz zur Kreyßigstraße geführt werden, wäre dies nicht mehr möglich. Neben der Kita Goetheplatz befindet sich die private Elterninitiative Wundertüte, für die das gleiche gilt. | Unabhängig von den Kita-Kindern sind von Frühling bis Herbst gefühlt alle Neustadt-Kinder mit ihren Eltern auf dem Goetheplatz. Freies Spiel für die Kinder und Entspannung für die Eltern wäre mit der Straßenbahn Geschichte. Der Goetheplatz wird ja häufig auch Goethepark genannt, denn er ist die einzige große Grünfläche in der Neustadt, die für Ballspiel, Frisbee, Slackline und Picknicks mit Familie und Freunden in diesem dicht besiedelten Stadtteil genutzt werden kann. Die Kinder und Erwachsenen der Neustadt benötigen diesen Park.','2022-09-14 13:38:06.595+02',9,2,'SRID=4326;POINT (8.255754 50.010669)','0','M2'),
	 ('Variante Hindenburgstraße am günstigsten: Erschließung der Neustadt mit Variante Hindenburgstraße am günstigsten, weil Wallau- und insbesondere Rheinstraße am Rand der neustadt liegen und fast nur zu einer Seite erschließen.','2022-09-14 13:38:06.602+02',2,0,'SRID=4326;POINT (8.260142 50.008064)','0','M1'),
	 ('Variante Rheinallee kann überzeugen: Nur die Variante Rheinallee kann überzeugen. Die Neustadt wird sonst kaputt gemacht damit. Kinder können nicht mehr auf Frauen- oder Goetheplatz spielen.','2022-09-14 13:38:06.583+02',3,0,'SRID=4326;POINT (8.254713 50.015593)','0','M7'),
	 ('Variante Wallaustraße ist spannend: Die Variante Wallaustraße ist spannend und man kommt auf diese nicht beim ersten Betrachten. Ist diese Straße jedoch breit genug, um auch den Fußgänger- und Radverkehr nicht zu behindern?','2022-09-14 13:38:06.587+02',0,1,'SRID=4326;POINT (8.257765 50.012638)','0','M1'),
	 ('Variante Wallaustraße nicht bis Ende Wallaustraße führen: Die Variante ''Wallaustraße'' könnte auch ab der Goethestraße nach links in Richtung Barbarossaring abbiegen (ohne Weiterführung durch die Wallaustraße bis zum Kaiser-Karl-Ring) und dann wieder in die Kreyßigstraße auf die Route der Variante ''Hindenburgstraße'' in Richtung Depot verschwenken. | Dies hätte ebenfalls den Vorteil einer Alternativanbindung des Depots ans Streckennetz, würde aber die mit Sicherheit schwierigere Verbindung von der Wallaustraße in den Kaiser-Karl-Ring, sowie die Engen Platzverhältnisse hinter der Feuerwache umgehen. Man hätte aber dennoch eine bessere Anbindung der nordwestlichen Neustadt erreicht.','2022-09-14 13:38:06.585+02',1,1,'SRID=4326;POINT (8.255936 50.013911)','0','M1'),
	 ('Verbindung Hechtsheim zum Höfchen: Können sie Aussagen darüber treffen, ob von Hechtsheim kommend auch zum Höfchen gefahren werden kann oder können sie das direkt ausschließen?','2022-09-14 13:38:06.609+02',1,0,'SRID=4326;POINT (8.272678 49.999323)','0','M1'),
	 ('Verkehr an den Grachten: Aus Richtung Landtag kommend rechts am REWE in die Straße An den Grachten (Parallelstraße Rheinallee) einbiegen und komplett durchfahren und dann kommt man an der Haltestelle Zollhafen raus.','2022-09-14 13:38:06.581+02',2,1,'SRID=4326;POINT (8.25394 50.016746)','0','M5'),
	 ('Verkehrsberuhigung Wallaustraße beibehalten: Die Unterbindung oder Reduzierung des Durchgangsverkehrs (wie an der Ecke Wallau-/Neckarstraße) sollte bei der Strecke durch die Wallaustraße beibehalten werden. Dies könnte bspw. mittels versenkbaren Pollern zwischen den Gleisen oder einem kurzen Abschnitt mit Rasengleis in dem Bereich umgesetzt werden.','2022-09-14 13:38:06.592+02',2,0,'SRID=4326;POINT (8.259883 50.011273)','0','M1'),
	 ('Verknüpfung Wallaustraße &lt;--&gt; Bauhofstraße: In der roten Vorschlagsvariante wird die Trasse von der Bauhofstraße über Kaiserstraße und 117er Ehrenhof auf die Wallaustraße geführt. Gibt es Gründe (Kurvenradius, Schulwege etc.), die einer Führung über Hindenburgplatz und Adam-Karrillon-Straße widersprechen?','2022-09-14 13:38:06.604+02',2,0,'SRID=4326;POINT (8.263538 50.006512)','0','M3');
INSERT INTO comment ("comment",created_at,likes,dislikes,geom,project_id,user_id) VALUES
	 ('Verlegung Haltestelle Straßenbahnamt: Verlegung der alten Haltestelle Straßenbahnamt Linie 76 / Linie 71 weiter nach vorne, somit wird die Umstiegsbeziehung von Bus / Tram gestärkt und vorallem die Linie 59 ersetzt, die nur wenn Semester ist fährt.','2022-09-14 13:38:06.582+02',0,0,'SRID=4326;POINT (8.253578 50.016306)','0','M5'),
	 ('Verlängerung ist notwerndig: Eine Verlängerung der Straßenbahn ab Kisselberg über Finthen Kurmainzstraße, Flugplatzstraße, Wackernheim, Ingelheim ist ebenfalls notwendig.','2022-09-14 13:38:06.614+02',5,0,'SRID=4326;POINT (8.148591 49.980933)','0','M2'),
	 ('Vom Individualverkehr unabhängige Trasse zu erstellen: Im Falle der Variante Rheinallee und der damit verbundenen Reisezeit wäre es am sinnvollsten eine vom individualverkehr unabhängige Trasse zu erstellen. führt dazu um die Geschwindigkeit auf 50km/h zu erhöhen und es würde die Reisezeit verkürzen und unabhängig vom Berufsverkehr zu sein. Nachteil die Rheinallee wird in beide Richtungen 1 Spurig und es führt zu noch mehr Staus','2022-09-14 13:38:06.591+02',2,0,'SRID=4326;POINT (8.261912 50.011808)','0','M1'),
	 ('Wallaustrasse - Die engste Variante vermeiden: Die Wallaustrasse ist, besonders im Bereich um die Feldbergstraße sowie im südlichen Verlauf, wohl die Option mit dem geringsten Abstand zwischen den gegenüberliegenden Wohnhäusern. | Die Bebauungssituation dort würde zu nicht unerheblichem Lärm zum Nachteil der Anwohner führen, der sich bei den anderen Varianten durch das Mehr an Platz viel besser verteilen kann. Weiterhin dürfte es in diesen Bereichen herausfordernd werden, zwei Bahnen aneinander vorbei zu führen und gleichzeitig ausreichend breite Gehwege zu haben. | Auch dürfte der Wegfall der Parkplätze den ohnehin schon hohen Parkdruck der Anwohner und Lieferanten in dieser Gegend weiter verschlimmern.','2022-09-14 13:38:06.595+02',7,2,'SRID=4326;POINT (8.260614 50.01077)','0','M3'),
	 ('Wallaustraße gute Alternative zur Hindenburgstraße: Die Variante Wallaustraße erscheint mir eine gute Alternative zur Hindenburgstraße. Könnten Sie deren Vorteile noch etwas herausarbeiten?','2022-09-14 13:38:06.593+02',0,3,'SRID=4326;POINT (8.260128 50.011106)','0','M1'),
	 ('Warum eine Trasse durch den Goethepark?: Ich halte eine Erweiterung des Straßenbahnnetzes für das richtige Vorgehen - für die Mobilität und auch für den Klimaschutz. Aber ich kann nicht nachvollziehen, wie man solch ein Projekt durch nur einen von zwei Parks in der Neustadt führen kann. Hierfür müssen weitere Bäume gefällt werden, von denen es sowieso schon viel zu wenige in der Stadt gibt, was wir ja in den letzten Sommern mit Rekordtemperaturen alle spüren konnten. Der Park is immer gut frequentiert von Familien, Jugendlichen und auch Älteren, die dort Federball, Fußball, Boule etc. spielen. Und hier soll eine Straßenbahntrasse durchgehen? Durch einen der wenigen Plätze in der Neustadt, an denen die Kinder mal frei rumrennen können? Ich empfehle den Planern dringen einen Besuch am Nachmittag und auch am Wochenende dort, um sich darüber ein Bild zu machen, was hier los ist - insbesondere auch an einem heißen Sommertag durch den Wasserspielplatz, der gerade von Familien mit Kleinkindern viel genutzt wird. Die Trasse soll darüber hinaus genau vor den Toren von zwei Kitas verlaufen und am Spielplatz lang, obwohl der Goehteplatz und die Hindenburgstraße aktuell durch mehrere Buslinien bereits sehr gut angebunden sind und es außer zu Schulbeginn und -ende dort keine Kapazitätsprobleme zu geben scheint - im Gegensatz zu den neuen Wohngebieten am Zollhafen, die teilweise recht weit von der nächsten Bushaltestellte entfernt sind. Hier auf der Rheinallee wäre der Mehrwert doch viel größer. Hier ist auch schon viel Verkehr, der durch eine Straßenbahn auf eigener Trasse einen echten Zeitvorteil für den ÖPNV bringen würde, wenn die Straßenbahn einfach am Stau vorbeifahren könnte.','2022-09-14 13:38:06.595+02',10,1,'SRID=4326;POINT (8.255281 50.010706)','0','M26'),
	 ('Warum fährt die Straßenbahn nicht zur Uniklinik: Warum fährt die Straßenbahn nicht zur Uniklinik','2022-09-14 13:38:06.612+02',1,0,'SRID=4326;POINT (8.25555 49.994616)','0','M1'),
	 ('Warum nicht gleich zwei der drei Varianten verwirklichen?: Ich finde gleich zwei der drei Varianten toll. Ich als jemand, der oft in der Neustadt ist und dort Freunde besucht und in den tollen Läden gern einkauft, finde dass man nicht nur eine Linie da entwickeln sollte. Mit der blauen und der lila Linie würde man die komplette Neustadt abdecken, und man müsste nie weiter als 10 Minuten zur nächsten Haltestelle laufen. Als weiteres Plus würde sich der Takt ab der Zusammenführung verdoppeln.','2022-09-14 13:38:06.61+02',5,4,'SRID=4326;POINT (8.271385 49.998893)','0','M10'),
	 ('Warum Quartier Hechtsheimer Str. nicht Prio 1?: Ich verstehe nicht, warum das neue Quartier Hechtsheimer Str. nicht Prio 1 ist. Die Erschließung VOR dem Bau einer neuen Siedlung begünstigt die Verkehrswende, da evtl. gar keine neuen Autos mehr angeschafft werden. Wenn das Auto mal da ist, fällt die Aufgabe schwerer...','2022-09-14 13:38:06.614+02',0,0,'SRID=4326;POINT (8.280224 49.980889)','0','M1'),
	 ('Warum über den Goetheplatz?: Der Goetheplatz ist gerade im Sommer extrem voll, u.a. auch mit spielenden Kindern. Direkt an der Trasse liegt der Spielplatz und zwei Kitas. Er ist einer von nur zwei grünen Plätzen in der Neustadt, hier fliegen Bälle und Frisbees. Wie kommt man auf die Idee über diesen Platz eine Straßenbahn zu führen? Ich stehe hinter dem Straßenbahnausbau, aber warum über den Goetheplatz? So was fällt nur jemandem ein, der nicht in der Neustadt lebt.','2022-09-14 13:38:06.596+02',2,0,'SRID=4326;POINT (8.255493 50.010485)','0','M6');
